"use strict";class Utilities{static getStoredSettings(){return new Promise((t=>{chrome.storage.local.get(["autoStartCycles","autoStartBreaks","cycleMinutes","breakMinutes","totalCycles"],(e=>{const s={autoStart:{cycles:e.autoStartCycles,breaks:e.autoStartBreaks},cycleTime:6e4*e.cycleMinutes,breakTime:6e4*e.breakMinutes,totalPeriods:2*e.totalCycles-1};t(s)}))}))}static msToMin(t){let e=Math.floor(t%36e5/6e4);return t>=36e5&&(e+=60),e}static msToSec(t){return Math.floor(t%6e4/1e3)}static parseMs(t){let e=this.msToMin(t).toString(),s=this.msToSec(t).toString();return 1===e.length&&(e=`0${e}`),1===s.length&&(s=`0${s}`),`${e}:${s}`}static mapCycle(t){return t/2+1}static mapBreak(t){return t/2+.5}}export{Utilities};